import Link from './Link'


function navProps () {
    return [
      { href: '/', text: 'Home', align: 'left', isActive: true},
      { href: '/about', text: 'About', align: 'right', isActive: false},
      { href: '/inventory', text: 'Inventory', align: 'right', isActive: false},
      { href: '/contact', text: 'Contact Us', align: 'right', isActive: false},
    ]
}

const NavLink = ({ navProp }) => (
        <li className={navProp.align == 'left' ? 'left' : null}>
            <Link activeClassName='active' href={navProp.href}>
                <a className={'nav-link' + navProp.isActive ? ' home-link' : ''}>
                    {navProp.text} 
                </a>
            </Link>
        <style jsx>{
        `.active {
            background-color: #0000FF;
        }
        .nav-link {
            text-decoration: none;
            padding: 10px;
            display: block;
        }
        ul.topnav li {
            float: right;
        }     
        ul.topnav li a {
            display: block;
            color: white;
            text-align: center;
            padding: 14px 16px;
            text-decoration: none;
            font-family: "Arial";
        }    
        ul.topnav li a:hover:not(.active) {
            background-color: #111;
        }
        ul.topnav li.left {
            float: left;
        }
        @media screen and (max-width: 600px) {
            ul.topnav li.left, 
            ul.topnav li {float: none;}           
        }`}
        </style>
    </li>
)
// background-color: #4CAF50;
const Header = () => (
    <div>
        <ul className="topnav">
        {navProps().map((navProp) => (
            <NavLink key={navProp.text} navProp={navProp}/>          
        ))}
        </ul>
        <style jsx>{`    
        ul.topnav {
            list-style-type: none;
            margin: 0;
            padding: 0;
            overflow: hidden;
            background-color: #333;
        }         
        `}</style>
    </div>
)

export default Header;
