import Link from './Link'


function navProps() {
    return [
        { href: '/', text: 'Home', align: 'left', isActive: true },
        { href: '/about', text: 'About', align: 'right', isActive: false },
        { href: '/inventory', text: 'Inventory', align: 'right', isActive: false },
        { href: '/contact', text: 'Contact Us', align: 'right', isActive: false },
    ]
}
const Header = () => (
    <div>
        <ul className="topnav">
            {navProps().map((navProp) => (
                <li>
                    <img src='logo.webp'/>
                </li>
            ))}
        </ul>
        <style jsx>{
            `body {
            margin: 0;
        }
        .active {
            background-color: #0000FF;
        }
        .nav-link {
            text-decoration: none;
            padding: 10px;
            display: block;
        }
        ul.topnav {
            list-style-type: none;
            margin: 0;
            padding: 0;
            overflow: hidden;
            background-color: #333;
        }
        ul.topnav li {
            float: right;
        }     
        ul.topnav li a {
            display: block;
            color: white;
            text-align: center;
            padding: 14px 16px;
            text-decoration: none;
            font-family: "Menlo";
        }    
        ul.topnav li a:hover:not(.active) {
            background-color: #111;
        }
        ul.topnav li.left {
            float: left;
        }
        @media screen and (max-width: 600px) {
            ul.topnav li.left, 
            ul.topnav li {float: none;}           
        }`}
        </style>
    </div>
)

export default Header;
