import Link from './Link'
import {withRouter} from "next/router";

const linkStyle = {
    marginRight: 15
}


function navProps () {
    return [
      { href: '/', text: 'Home', align: 'left', isActive: true},
      { href: '/about', text: 'About', align: 'right', isActive: false},
      { href: '/inventory', text: 'Inventory', align: 'right', isActive: false},
      { href: '/contact', text: 'Contact Us', align: 'right', isActive: false},
    ]
}

const NavLink = ({ navProp }) => (
    <li>
        <Link href={navProp.href}>
            <a>{navProp.text}</a>
        </Link>
        <style jsx>{
        `li {
            list-style: none;
            margin: 5px 0;
            float: left;
        }    
        a {
            text-decoration: none;
            color: black;
            font-family: "Arial";
            display: block;
            padding: 8px;
        }    
        a:hover {
            opacity: 0.6;
        }`}
        </style>
    </li>
)
const NavLinks = () => (
    <ul>
        {navProps().map((navProp) => (
            <NavLink key={navProp.text} navProp={navProp}/>
        ))}
    </ul>

)
const handleClick = (navProp) => {
    if (!navProp.isActive) {
        navProp.isActive = true;
    }
}
const Header = () => (
    <div>
        <ul>
        {navProps().map((navProp) => (
            <li style={{float:navProp.align}}>
                <Link activeClassName='active' href={navProp.href}>
                    <a className={'nav-link' + navProp.isActive ? ' home-link' : ''}>
                        {navProp.text} 
                    </a>
                </Link>
            </li>           
        ))}
        </ul>
        <style jsx>{`    
        .active:after {
            background-color: #4CAF50;
            content: ' (current page)';
        }
        .nav-link {
            text-decoration: none;
            padding: 10px;
            display: block;
          }     
        li a {
            display: block;
            color: white;
            text-align: center;
            padding: 14px 16px;
            text-decoration: none;
            font-family: "Arial";
        }    
        li a:hover {
            background-color: #111;
        }
        ul {
            list-style-type: none;
            margin: 0;
            padding: 0;
            overflow: hidden;
            background-color: #333;
        }         
        `}</style>
    </div>
)

export default withRouter((Header));
