import Link from 'next/link'

const linkStyle = {
    marginRight: 15
}


function navProps () {
    return [
      { href: '/', text: 'Home'},
      { href: '/about', text: 'About'},
      { href: '/inventory', text: 'Inventory'},
    ]
}

const NavLink = ({ navProp }) => (
    <li>
        <Link href={navProp.href}>
            <a>{navProp.text}</a>
        </Link>
        <style jsx>
        {`li {
            list-style: none;
            margin: 5px 0;
            float: left;
        }    
        a {
            text-decoration: none;
            color: white;
            font-family: "Arial";
            display: block;
            padding: 8px;
        }    
        a:hover {
            opacity: 0.6;
        }`}
        </style>
    </li>
)
const NavLinks = () => (
    <ul>
        {navProps().map((navProp) => (
            <NavLink key={navProp.text} navProp={navProp}/>
        ))}
        <style jsx>{`
        ul {
            list-style-type: none;
            margin: 0;
            padding: 0;
            background-color: #dddddd;
        }         
        `}</style>
    </ul>

)

const Header = () => (
    <div>
        <NavLinks />
    </div>
)

export default Header
