import Link from 'next/link'
import window from 'global'

const linkStyle = {
    marginRight: 15
}


function navProps () {
    return [
      { href: '/', text: 'Home', align: 'left'},
      { href: '/about', text: 'About', align: 'right'},
      { href: '/inventory', text: 'Inventory', align: 'right'},
      { href: '/contact', text: 'Contact Us', align: 'right'},
    ]
}

const NavLink = ({ navProp }) => (
    <li>
        <Link href={navProp.href}>
            <a>{navProp.text}</a>
        </Link>
        <style jsx>{
        `li {
            list-style: none;
            margin: 5px 0;
            float: left;
        }    
        a {
            text-decoration: none;
            color: black;
            font-family: "Arial";
            display: block;
            padding: 8px;
        }    
        a:hover {
            opacity: 0.6;
        }`}
        </style>
    </li>
)
const NavLinks = () => (
    <ul>
        {navProps().map((navProp) => (
            <NavLink key={navProp.text} navProp={navProp}/>
        ))}
    </ul>

)
// style={`float:${navProp.align}`}

const Header = () => (
    <div>
        <ul>
        {navProps().map((navProp) => (
            <li>
                <Link href={navProp.href}>
                    <a className={window.location.pathname == navProp.href ? 'active' : null}>
                        {navProp.text} 
                    </a>
                </Link>
            </li>           
        ))}
        </ul>
        <style jsx>{`    
        .active {
            background-color: #4CAF50;
        }    
        li {
            float: left;
        }  
        li a {
            display: block;
            color: white;
            text-align: center;
            padding: 14px 16px;
            text-decoration: none;
            font-family: "Arial";
        }    
        li a:hover {
            background-color: #111;
        }
        ul {
            list-style-type: none;
            margin: 0;
            padding: 0;
            overflow: hidden;
            background-color: #333;
        }         
        `}</style>
    </div>
)

export default Header
